"use strict";(self.webpackChunk_dreamcatcher_tech_dreamcatcher_website=self.webpackChunk_dreamcatcher_tech_dreamcatcher_website||[]).push([[9809],{4067:(e,t,a)=>{a.r(t),a.d(t,{assets:()=>r,contentTitle:()=>s,default:()=>c,frontMatter:()=>o,metadata:()=>l,toc:()=>h});var i=a(7462),n=(a(7294),a(3905));a(1839);const o={},s="R19 T83 Resilient Warship",l={unversionedId:"Requests/R19",id:"Requests/R19",title:"R19 T83 Resilient Warship",description:"Warship firing missiles at night",source:"@site/dust/Requests/R19.md",sourceDirName:"Requests",slug:"/Requests/R19",permalink:"/dust/Requests/R19",draft:!1,editUrl:"https://github.com/dreamcatcher-tech/dreamcatcher-tech.github.io/edit/master/website/dust/Requests/R19.md",tags:[],version:"current",frontMatter:{},sidebar:"dust",previous:{title:"R18 Name Resolution System for Interblock",permalink:"/dust/Requests/R18"},next:{title:"R20 Dreamcatcher Simulation Toolbox",permalink:"/dust/Requests/R20"}},r={},h=[{value:"Targets",id:"targets",level:2},{value:"Dependencies",id:"dependencies",level:2},{value:"Background",id:"background",level:2},{value:"Type 83 Destroyer",id:"type-83-destroyer",level:3},{value:"The Challenge of the Design",id:"the-challenge-of-the-design",level:3},{value:"Strategic Intent of this Request",id:"strategic-intent-of-this-request",level:2},{value:"Tactical Intent",id:"tactical-intent",level:2},{value:"Detailed Description",id:"detailed-description",level:2},{value:"Tactical Intent 1: Maintain normal operations in peace time while minimising the need for specialist repair alongside due to component failure at sea.",id:"tactical-intent-1-maintain-normal-operations-in-peace-time-while-minimising-the-need-for-specialist-repair-alongside-due-to-component-failure-at-sea",level:3},{value:"Must",id:"must",level:4},{value:"Should",id:"should",level:4},{value:"Could",id:"could",level:4},{value:"Should Not",id:"should-not",level:4},{value:"Must not",id:"must-not",level:4},{value:"Tactical Intent 2: Continue the collection and dissemination of radar data, satelite communications data to the wider network-centric view of the fleet following a single missile strike.",id:"tactical-intent-2-continue-the-collection-and-dissemination-of-radar-data-satelite-communications-data-to-the-wider-network-centric-view-of-the-fleet-following-a-single-missile-strike",level:3},{value:"Must",id:"must-1",level:4},{value:"Should",id:"should-1",level:4},{value:"Could",id:"could-1",level:4},{value:"Should Not",id:"should-not-1",level:4},{value:"Tactical Intent 3: Allow the continuation of the &#39;external war&#39; despite sustained damage.",id:"tactical-intent-3-allow-the-continuation-of-the-external-war-despite-sustained-damage",level:3},{value:"Must",id:"must-2",level:4},{value:"Should",id:"should-2",level:4},{value:"Could",id:"could-2",level:4},{value:"Should Not",id:"should-not-2",level:4},{value:"Tactical Intent 4: Allow the continuation of the &#39;internal war&#39; - the damage control effort - despite sustained damage.",id:"tactical-intent-4-allow-the-continuation-of-the-internal-war---the-damage-control-effort---despite-sustained-damage",level:3},{value:"Must",id:"must-3",level:4},{value:"Should",id:"should-3",level:4},{value:"Could",id:"could-3",level:4},{value:"Should Not",id:"should-not-3",level:4}],d={toc:h};function c(e){let{components:t,...o}=e;return(0,n.kt)("wrapper",(0,i.Z)({},d,o,{components:t,mdxType:"MDXLayout"}),(0,n.kt)("h1",{id:"r19-t83-resilient-warship"},"R19 T83 Resilient Warship"),(0,n.kt)("p",null,(0,n.kt)("img",{alt:"Warship firing missiles at night",src:a(1694).Z,width:"256",height:"256"}),"  "),(0,n.kt)("h2",{id:"targets"},"Targets"),(0,n.kt)("ol",null,(0,n.kt)("li",{parentName:"ol"},(0,n.kt)("a",{parentName:"li",href:"/dust/Requests/R06"},"R06 Dreamcatcher"))),(0,n.kt)("h2",{id:"dependencies"},"Dependencies"),(0,n.kt)("h2",{id:"background"},"Background"),(0,n.kt)("h3",{id:"type-83-destroyer"},"Type 83 Destroyer"),(0,n.kt)("p",null,"The ",(0,n.kt)("a",{parentName:"p",href:"https://www.forces.net/news/type-83-destroyer-what-we-know-so-far"},"Type 83 Destroyer"),", the replacement for the ",(0,n.kt)("a",{parentName:"p",href:"https://en.wikipedia.org/wiki/Type_45_destroyer"},"Type 45 Daring Class Destroyer")," currently in use with the Royal Navy, begins its design phase in 2022 with a planned deployment in the early 2030s."),(0,n.kt)("p",null,"Under current plans, the RN\u2019s six Type 45 destroyers commissioned between 2009-13 will ",(0,n.kt)("a",{parentName:"p",href:"https://ukdefencejournal.org.uk/type-45-destroyers-out-of-service-dates-revealed/"},"leave service between 2035 and 2038"),". Designing and funding their replacements presents a challenging task as the ",(0,n.kt)("a",{parentName:"p",href:"https://www.gov.uk/government/publications/defence-in-a-competitive-age/defence-in-a-competitive-age-accessible-version"},"above-water battlespace is becoming increasingly dangerous and complex"),"."),(0,n.kt)("p",null,"The weapons capability of the T83 is likely to include some form of ",(0,n.kt)("a",{parentName:"p",href:"https://www.defensenews.com/naval/2022/06/21/lockheed-martin-blends-ai-decision-aide-virtual-aegis-combat-system-in-drill-near-guam/"},"AI driven Fire Decision Assistance"),", ",(0,n.kt)("a",{parentName:"p",href:"https://www.hindawi.com/journals/complexity/2018/6201356/"},"AI assisted Network-Centric Communications")," (where there is a shared view of the entire battlespace between all platforms), and ",(0,n.kt)("a",{parentName:"p",href:"https://www.jhuapl.edu/Content/techdigest/pdf/V22-N04/22-04-Landis.pdf"},"Fire Control Handoff")," (where one ship can effectively fire the weapons of a second ship using data that it has gathered.)"),(0,n.kt)("p",null,"Internally, it's likely that the T83 will follow previous designs with ",(0,n.kt)("a",{parentName:"p",href:"https://seawaves.com/?p=17371"},"integrated data-only communications system")," incorporating all activities from satellite, high-frequency radio, radar target aquisition and fire control to secure voice and damage control operations. Likewise it's likely it will follow previous designs in deploying an ",(0,n.kt)("a",{parentName:"p",href:"https://www.gepowerconversion.com/case-study/type-45-destroyer-daring-class-worlds-first-full-electric-propulsion-combatant-ship"},"all electric propusion fitout")," where there is no direct engine-to-driveshaft connection and therefore relies heavily on that same data network."),(0,n.kt)("p",null,"As a weapons platform, it's helpful to consider the Type 45 which extended the previous capability by the inclusion of the ",(0,n.kt)("a",{parentName:"p",href:"https://www.baesystems.com/en/sampson-multi-function-radar"},"Sampson Radar system")," linked to ",(0,n.kt)("a",{parentName:"p",href:"https://en.wikipedia.org/wiki/PAAMS"},"PAAMS (Principal Anti Air Missile System)"),", which is designed to protect an area of sea around a Task Group against high performance threats such as low altitude missile saturation attacks, supersonic anti-ship cruise missiles, fighter aircraft and UAVs.  The complement of 48 ",(0,n.kt)("a",{parentName:"p",href:"https://www.baesystems.com/en/product/vertical-launching-system-vls-mk41"},"VLS (Vertical Launch System)")," ",(0,n.kt)("a",{parentName:"p",href:"https://www.mbda-systems.com/product/aster-15-30/"},"Aster missiles")," available to it is already being considered too small given the military doctrine of both Russia and China, who favour ",(0,n.kt)("a",{parentName:"p",href:"https://www.rand.org/content/dam/rand/pubs/perspectives/PE200/PE231/RAND_PE231.pdf"},"massed anti-ship missile attacks"),".  The fact that the Type 45 successor has been named as Type 83 hints that the T83 will therefore be substantially larger, and likely to have a considerably larger complement of VLS missiles."),(0,n.kt)("p",null,"On future weapons development over the operational period of the T83, it's likely that a new generation of weapons, notably rail gun systems (",(0,n.kt)("a",{parentName:"p",href:"https://www.navyrecognition.com/index.php/naval-news/naval-news-archive/2018/january-2018-navy-naval-defense-news/5880-russia-continues-r-d-work-on-electromagnetic-railgun.html"},"which continue to be developed by both China and Russia"),") ",(0,n.kt)("a",{parentName:"p",href:"https://www.jpost.com/international/article-705006"},"hypersonic anti-ship missiles")," and ",(0,n.kt)("a",{parentName:"p",href:"https://www.fpri.org/article/2021/05/chinas-anti-ship-ballistic-missile-capability-in-the-south-china-sea/"},"ballistic anti-ship missiles")," will increasingly put surface combatants at much greater risk. Any platform that is going to provide credible defence for a Task Group against these threats will inevitably have to be sophisticated, large and substantially more automated in its Fire Control and Operations Functions. "),(0,n.kt)("p",null,"The Royal Navy continues to lead the way in ",(0,n.kt)("a",{parentName:"p",href:"https://www.navaltoday.com/2022/03/16/uk-plans-to-test-laser-weapon-on-type-23-frigate-for-the-first-time/"},"Directed Energy Weapons (DEW)")," as ",(0,n.kt)("a",{parentName:"p",href:"https://en.wikipedia.org/wiki/Close-in_weapon_system"},"CIWS (Close-in Weapons Systems)"),", and appears to be on track for deployment mid-2030s.  Although ",(0,n.kt)("a",{parentName:"p",href:"https://www.defensenews.com/naval/2021/07/01/us-navy-ditches-futuristic-railgun-eyes-hypersonic-missiles/"},"rail gun development in NATO has stalled"),", USN, China and Russia continue to develop ",(0,n.kt)("a",{parentName:"p",href:"https://www.navalnews.com/naval-news/2022/06/u-s-navys-hypersonic-missile-will-give-zumwalt-class-new-capability/"},"hypersonic missiles"),", and with previous agreements with USN it's ",(0,n.kt)("a",{parentName:"p",href:"https://www.royalnavy.mod.uk/news-and-latest-activity/news/2022/june/14/220614-london-tech-bridge-launch"},"reasonable to expect that technology between these two navys will continue")," This may see DEW, Hypersonic and ",(0,n.kt)("a",{parentName:"p",href:"https://ukdefencejournal.org.uk/counter-hypersonic-capability-key-feature-of-type-83-destroyer/"},"Counter-Hypersonic Weapons systems")," being integrated on the T83 alongside continued development in the ",(0,n.kt)("a",{parentName:"p",href:"file:///Users/malcolm/Downloads/V25N3%20-%20Riding%20The%20Next%20Wave%20of%20Network-Centric%20Warfare.pdf"},"Electronic and Network-centric warfare systems")," being shared between the two forces.  This will inevitably lead to a growing reliance on data network integrity both in-ship and between ships in a joint NATO fleet."),(0,n.kt)("blockquote",null,(0,n.kt)("p",{parentName:"blockquote"},"\u201cThe next decade is likely to see a \u2018Dreadnought moment\u2019 in relation to war at sea stimulated by radically novel technologies. Despite its massive superiority, the US will have to continue to invest heavily to maintain its lead while other ambitious navies, notably China and Russia are likely to follow closely. The rest will struggle to remain within touching distance, especially as they doggedly persist with traditional ship programmes and designs well into the future. As a result, the world will be divided into countries that can prevail at sea and those that, frankly, need not bother\u201d "),(0,n.kt)("blockquote",{parentName:"blockquote"},(0,n.kt)("p",{parentName:"blockquote"},"Rear Admiral Chris Parry, RN Retd, speaking to Royal United Services Institute, Mar 2021",(0,n.kt)("br",{parentName:"p"}),"\n",(0,n.kt)("a",{parentName:"p",href:"https://www.navylookout.com/the-type-83-destroyer-the-royal-navys-future-anti-air-warfare-combatant/"},"(Reference)")))),(0,n.kt)("h3",{id:"the-challenge-of-the-design"},"The Challenge of the Design"),(0,n.kt)("p",null,"Details of the Type 83 are not publically available; however, conclusions can be drawn from the Type 45.  "),(0,n.kt)("p",null,"Although considered the most capable Destroyer in the world today, the weaknesses in the Type 45 are evident when considering it's layout and dependence on communications, both internal and external, and its reliance on electric propusion which distributes power and proplusion throughout the ship."),(0,n.kt)("p",null,"The role of Destroyers are to act as a shield, which involves being deployed up-threat, defending against and if necessary taking strikes from an incoming attack in order for the main Task Group, generally in the RN's case a Task Group centred on a Queen Elizabeth class Aircraft Carrier, to continue on-task."),(0,n.kt)("p",null,"As such, a Destroyer's task in a battle situation tends to be one where it is likely to be the first to sustain damage, but who's role is to continue to prevent damage to the main fleet regardless of damage sustained to itself.  Should that fail, removing a Destroyer as an effective platform leverages the ability of the enemy to prosecute the engagement on the primary target far more effectively."),(0,n.kt)("p",null,"As an example, consider the layout of the Type 45:"),(0,n.kt)("p",null,(0,n.kt)("img",{alt:"HMS DARING LAYOUT",src:a(6524).Z,width:"500",height:"346"}),"  "),(0,n.kt)("p",null,"A majority of the hardware necessary to continue that comms and data transmission is centralised above the water-line in the main super-structure.  Not shown here are two server clusters, one forward, one aft.  However, both serve the main super-structure island.  "),(0,n.kt)("p",null,"The vulnerability is then that a powerful and effective platform may be rendered ineffective by a single missile striking midships, a rather likely situation should any missile strike get through."),(0,n.kt)("p",null,"Because of this Destroyer design focus has been on prevention of that strike - on the effective deployment of CIWS for example.  However, technology has not been forthcoming that would allow such a sophisticated ship to continue fighting beyond that first strike."),(0,n.kt)("p",null,"Contrast this with the designs available during WW2, where it was not unusual for a Heavy Cruiser (the equivalent size of vessel then) to ",(0,n.kt)("a",{parentName:"p",href:"https://www.world-war.co.uk/spee.php"},"sustain several sustained hits and continue fighting"),"."),(0,n.kt)("p",null,"Warships now are therefore now generally both far more lethal and far more fragile than 80 years ago."),(0,n.kt)("p",null,"In terms of ROI, each T45 has cost roughly \xa31.050 billion to build.  Typically 2 would be deployed defending a Queen Elizabeth Class aircraft carrier (cost \xa33 billion).  With additional Frigates for anti-submarine warfare, a Task Group, who's ability to project force lies primarily with the aircraft carrier, may well cost \xa310 billion+.  "),(0,n.kt)("p",null,"Should a T45 be disabled by a single missile strike and be unable to continue its mission the Task Group would be exposed on that side making a strike on the primary target more probable.  Should that happen the total cost would be roughly \xa31bn + \xa33bn + loss of capability in the current campaign (ignoring of course the human life involved.)"),(0,n.kt)("p",null,"If a T45 could continue to protect the capital ship following a single missile strike, even if subsequently it needs to return to port, then that cost reduces to \xa31bn, with far fewer casualties."),(0,n.kt)("p",null,"Given the current Chinese and Russian military doctrine of combatting missile defences through massed strikes, it's probable that more than one anti-ship missile will get through.  If the Destroyer continues to be available the cost remains static until that ship can no longer provide the cover to the capital ship.  It's dependent therefore on the scenario whether the enemy has sufficient incoming ordnance to acheive it's aim.  However, it's certain that a Destroyer that can continue to fight beyond a single missile strike damage is significantly more valuable that one that cannot."),(0,n.kt)("h2",{id:"strategic-intent-of-this-request"},"Strategic Intent of this Request"),(0,n.kt)("p",null,"Provide a data comms and processing capability that can be deployed on a warship at sea, with the expectation that it can survive one or more strikes from an Anti-ship or Cruise missile and continue on-task, where 'survive' is defined as the abilty to continue providing a platform for those weapon systems that have locally survived the strike but which depend on non-local data connections and therefore control.  "),(0,n.kt)("p",null,"Moreover, it should be robust in normal peace-time use (minimising the need for specialist repair.) "),(0,n.kt)("h2",{id:"tactical-intent"},"Tactical Intent"),(0,n.kt)("p",null,"To do this, we need to:"),(0,n.kt)("ol",null,(0,n.kt)("li",{parentName:"ol"},"Maintain normal operations in peace time while minimising the need for specialist repair alongside due to component failure at sea."),(0,n.kt)("li",{parentName:"ol"},"Continue the collection and dissemination of radar data, satelite communications data to the wider network-centric view of the fleet following a single missile strike."),(0,n.kt)("li",{parentName:"ol"},"Allow the continuation of the 'external war' despite sustained damage."),(0,n.kt)("li",{parentName:"ol"},"Allow the continuation of the 'internal war' - the damage control effort - despite sustained damage.")),(0,n.kt)("h2",{id:"detailed-description"},"Detailed Description"),(0,n.kt)("h3",{id:"tactical-intent-1-maintain-normal-operations-in-peace-time-while-minimising-the-need-for-specialist-repair-alongside-due-to-component-failure-at-sea"},"Tactical Intent 1: Maintain normal operations in peace time while minimising the need for specialist repair alongside due to component failure at sea."),(0,n.kt)("h4",{id:"must"},"Must"),(0,n.kt)("ol",null,(0,n.kt)("li",{parentName:"ol"},"Be interoperable with all ship systems, many of which will be defined by others and may not have an API, routing instead through trusted network connections."),(0,n.kt)("li",{parentName:"ol"},"Be air-gapped with the wider world.")),(0,n.kt)("h4",{id:"should"},"Should"),(0,n.kt)("ol",null,(0,n.kt)("li",{parentName:"ol"},"Not require specialist software expertise while deployed at sea."),(0,n.kt)("li",{parentName:"ol"},"Have a secure and auditable route for upgrades."),(0,n.kt)("li",{parentName:"ol"},"Allow for Apps to be tamper-evident, and fail-safe")),(0,n.kt)("h4",{id:"could"},"Could"),(0,n.kt)("ol",null,(0,n.kt)("li",{parentName:"ol"},"Provide rolling upgrades while at-sea without the need for maintenance alongside.")),(0,n.kt)("h4",{id:"should-not"},"Should Not"),(0,n.kt)("ol",null,(0,n.kt)("li",{parentName:"ol"},"Allow any upgrades or alterations to the base operation without notification or requirement for authorisation.")),(0,n.kt)("h4",{id:"must-not"},"Must not"),(0,n.kt)("ol",null,(0,n.kt)("li",{parentName:"ol"},"Introduce vulnerabilites in terms of attack vectors available to opposing forces.")),(0,n.kt)("h3",{id:"tactical-intent-2-continue-the-collection-and-dissemination-of-radar-data-satelite-communications-data-to-the-wider-network-centric-view-of-the-fleet-following-a-single-missile-strike"},"Tactical Intent 2: Continue the collection and dissemination of radar data, satelite communications data to the wider network-centric view of the fleet following a single missile strike."),(0,n.kt)("h4",{id:"must-1"},"Must"),(0,n.kt)("ol",null,(0,n.kt)("li",{parentName:"ol"},"Allow verifiably correct data to be ingested by other nodes in the network or fail completely.  I.e. the data gets through or it does not, there is no 'try'."),(0,n.kt)("li",{parentName:"ol"},"Likewise, continue to receive and validate data from other platforms up to the point of failure, at which point failure is evident.")),(0,n.kt)("h4",{id:"should-1"},"Should"),(0,n.kt)("ol",null,(0,n.kt)("li",{parentName:"ol"},"Continue contact through multiple routes, including satelite, UHF, VHF and Microwave connections, such that the signal gets through so long at at least one route is available.")),(0,n.kt)("h4",{id:"could-1"},"Could"),(0,n.kt)("ol",null,(0,n.kt)("li",{parentName:"ol"},"Reduce the overall cost on-board of separate data systems requiring bespoke design and maintenance.")),(0,n.kt)("h4",{id:"should-not-1"},"Should Not"),(0,n.kt)("ol",null,(0,n.kt)("li",{parentName:"ol"},"Degrade the jitter levels currently experienced on command signals, where jitter in-ship is roughly 2ms on-board and packet loss <1%.  Latency is assumed owing to the short distances involved. ")),(0,n.kt)("h3",{id:"tactical-intent-3-allow-the-continuation-of-the-external-war-despite-sustained-damage"},"Tactical Intent 3: Allow the continuation of the 'external war' despite sustained damage."),(0,n.kt)("h4",{id:"must-2"},"Must"),(0,n.kt)("ol",null,(0,n.kt)("li",{parentName:"ol"},"Be capable of the Opps Room engaging incoming targets for self protection using at least one weapon system following a single missile strike which removes the comms room capability, assuming those weapon systems have not been locally compromised.")),(0,n.kt)("h4",{id:"should-2"},"Should"),(0,n.kt)("ol",null,(0,n.kt)("li",{parentName:"ol"},"Provide a weapons platform for the protection of the Task Group, where those weapon systems and their operation have not been compromised.")),(0,n.kt)("h4",{id:"could-2"},"Could"),(0,n.kt)("h4",{id:"should-not-2"},"Should Not"),(0,n.kt)("h3",{id:"tactical-intent-4-allow-the-continuation-of-the-internal-war---the-damage-control-effort---despite-sustained-damage"},"Tactical Intent 4: Allow the continuation of the 'internal war' - the damage control effort - despite sustained damage."),(0,n.kt)("h4",{id:"must-3"},"Must"),(0,n.kt)("ol",null,(0,n.kt)("li",{parentName:"ol"},"Fail gracefully, in that assuming an on-going damage control effort onboard there may be an increasing degradation in the ship's ability to fight depending on the effectiveness of damage control efforts."),(0,n.kt)("li",{parentName:"ol"},"Continue to provide internal communications between sections of the ship in order to direct Damage Control Teams and advise the chain of command of the current remaining capabilities of the ship.")),(0,n.kt)("h4",{id:"should-3"},"Should"),(0,n.kt)("ol",null,(0,n.kt)("li",{parentName:"ol"},"Provide information on the minute-by-minute situation onboard following a strike to inform future ship design. ")),(0,n.kt)("h4",{id:"could-3"},"Could"),(0,n.kt)("h4",{id:"should-not-3"},"Should Not"))}c.isMDXComponent=!0},6524:(e,t,a)=>{a.d(t,{Z:()=>i});const i=a.p+"assets/images/hms_daring_type_45_destroyer-43340-ced8bd5d4b2cde2686a3af9130777171.jpeg"},1694:(e,t,a)=>{a.d(t,{Z:()=>i});const i=a.p+"assets/images/T83-accd643d95e816684e76981f3d7a0cec.png"}}]);